import React, { useState, useEffect } from 'react';

function App() {
  const [inventory, setInventory] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    quantity: 0,
    price: 0,
  });
  const [editItemId, setEditItemId] = useState(null);

  // useEffect to update the page title when inventory changes
  useEffect(() => {
    document.title = `Inventory Management (${inventory.length} items)`;
  }, [inventory]);

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAddItem = () => {
    setInventory([...inventory, formData]);
    setFormData({ name: '', quantity: 0, price: 0 });
  };

  const handleEditItem = (itemIndex) => {
    setEditItemId(itemIndex);
    const itemToEdit = inventory[itemIndex];
    setFormData({ ...itemToEdit });
  };

  const handleUpdateItem = () => {
    const updatedInventory = [...inventory];
    updatedInventory[editItemId] = formData;
    setInventory(updatedInventory);
    setFormData({ name: '', quantity: 0, price: 0 });
    setEditItemId(null);
  };

  const handleDeleteItem = (itemIndex) => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      const updatedInventory = [...inventory];
      updatedInventory.splice(itemIndex, 1);
      setInventory(updatedInventory);
    }
  };

  return (
    <div>
      <h1>Inventory Management</h1>
      <div>
        <h2>Add New Item</h2>
        <input
          type="text"
          name="name"
          placeholder="Item Name"
          value={formData.name}
          onChange={handleFormChange}
        />
        <input
          type="number"
          name="quantity"
          placeholder="Quantity"
          value={formData.quantity}
          onChange={handleFormChange}
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={formData.price}
          onChange={handleFormChange}
        />
        {editItemId !== null ? (
          <button onClick={handleUpdateItem}>Update Item</button>
        ) : (
          <button onClick={handleAddItem}>Add Item</button>
        )}
      </div>
      <div>
        <h2>Inventory</h2>
        <ul>
          {inventory.map((item, index) => (
            <li key={index}>
              {item.name} (Quantity: {item.quantity}, Price: ${item.price})
              <button onClick={() => handleEditItem(index)}>Edit</button>
              <button onClick={() => handleDeleteItem(index)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
