import React, { useRef, useEffect, useState } from "react";

const Inventory = () => {
  const [items, setItems] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    quantity: 0,
    price: 0,
  });

  const [editItem, setEditItem] = useState(null);

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAddItem = () => {
    setItems([...items, formData]);
    setFormData({name: '', quantity: 0, price: 0});
    
  }

  const handleUpdateItem = () => {
    const updatedItem = [...items];
    // console.log(updatedItem);
    updatedItem[editItem] = formData;
    // console.log(updatedItem);
    setItems(updatedItem);
    setFormData({name: '', quantity: 0, price: 0});
    setEditItem(null);

  };

  const handleEditItem = (itemKey) =>{
      setFormData(items[itemKey]);
      setEditItem(itemKey);
  };

  const handleDeleteItem = (itemKey) =>{
    if(alert("Are you sure  to delete this ")){
        setItems(items.filter((_,index) => index !== itemKey))
    }
  }


  return (
    <div>
      <h1>Inventory Management</h1>
      <div>
        <h2>Add New Item</h2>
        <input
          type="text"
          name="name"
          placeholder="Item Name"
          value={formData.name}
          onChange={handleFormChange}
        />
        <input
          type="number"
          name="quantity"
          placeholder="Quantity"
          value={formData.quantity}
          onChange={handleFormChange}
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={formData.price}
          onChange={handleFormChange}
        />
        {editItem !== null ? (
          <button onClick={handleUpdateItem}>Update Item</button>
        ) : (
          <button onClick={handleAddItem}>Add Item</button>
        )}
      </div>
      <div>
        <h2>Inventory</h2>
        <ul>
          {items.map((item, index) => (
            <li key={index}>
              {item.name} Quantity: {item.quantity}, Price: ${item.price}
              <button onClick={() => handleEditItem(index)}>Edit</button>
              <button onClick={() => handleDeleteItem(index)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};
export default Inventory;
