import React, { useRef, useEffect, useState } from "react";

const Inventory = () => {
  const [items, setItems] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    quantity: 0,
    price: 0,
  });

  const [editItem, setEditItem] = useState(null);

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAddItem = () => {
    setItems([...items, formData]);
    setFormData({name: '', quantity: 0, price: 0});
    
  }

  const handleUpdateItem = () => {
    const updatedInventory = [...items];
    updatedInventory[editItem] = formData;
    setItems(updatedInventory);
    setFormData({ name: '', quantity: 0, price: 0 });
    setEditItem(null);

  };

  const handleEditItem = (itemKey) =>{

  };

  const handleDeleteItem = (itemKey) =>{

  }


  return (
    <div className="container">
      <h1 className="title text-center">Inventory List</h1>
      <form className="d-flex">
        
        <input
          type="text"
          name="name"
          placeholder="Item Name"
          value={formData.name}
          onChange={handleFormChange}
        />
        <input
          type="number"
          name="quantity"
          placeholder="Quantity"
          value={formData.quantity}
          onChange={handleFormChange}
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={formData.price}
          onChange={handleFormChange}
        />
        {editItem === null ? (
          
          <button onClick={handleAddItem}>Add Item</button>
        ) : (
            <button onClick={handleUpdateItem}>Update Item</button>
        )}
      </form>
      <ul className="container text-left">
        {items.map((item, keys)=>(
            <li key={keys}>
                {item.name} Quantity: {item.qauntity} Price: {item.price}
              <button onClick={() => handleEditItem(key)}>Edit</button>
              <button onClick={() => handleDeleteItem(key)}>Delete</button>
            </li>
        ))}
      </ul>
    </div>
  );
};
export default Inventory;
