import React, { useState } from "react";
import "./Inventory.css";

const Inventory = () => {
  const [items, setItems] = useState([]);
  const initialValue = {
    name: "",
    quantity: 0,
    price: 0,
  };
  const [formData, setFormData] = useState(initialValue);

  const [editItem, setEditItem] = useState(null);

  const [errors, setErrors] = useState({}); // Added errors state

  const validateValues = (data) => {
    let validationErrors = {};
    if (data.name === "") {
      validationErrors.name = "Please enter Item Name";
    }
    if (data.quantity <= 0) {
      validationErrors.quantity = "Quantity must be greater than 0";
    }
    if (data.price <= 0) {
      validationErrors.price = "Price must be greater than 0";
    }
    return validationErrors;
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    setErrors({}); // Clear errors when the user makes changes
  };

  const handleAddItem = () => {
    const validationErrors = validateValues(formData);
    if (Object.keys(validationErrors).length === 0) {
      setItems([...items, formData]);
      setFormData(initialValue);
    } else {
      setErrors(validationErrors); // Set validation errors in state
    }
  };

  const handleUpdateItem = () => {
    const validationErrors = validateValues(formData);
    if (Object.keys(validationErrors).length === 0) {
      const updatedItem = [...items];
      updatedItem[editItem] = formData;
      setItems(updatedItem);
      setFormData(initialValue);
      setEditItem(null);
    } else {
      setErrors(validationErrors); // Set validation errors in state
    }
  };

  const handleEditItem = (itemKey) => {
    setFormData(items[itemKey]);
    setEditItem(itemKey);
  };

  const handleDeleteItem = (itemKey) => {
    if (window.confirm("Are you sure, you want to delete this item")) {
      setItems(items.filter((_, index) => index !== itemKey));
    }
  };

  return (
    <div className="container">
      <h1>Inventory Management</h1>
      <div>
        <h2>Add New Item</h2>
        <label>
          Item Name:
          <input
            type="text"
            name="name"
            placeholder="Item Name"
            value={formData.name}
            onChange={handleFormChange}
          />
          {errors.name && <div className="error">{errors.name}</div>}
        </label>
        <label>
          Item Quantity:
          <input
            type="number"
            name="quantity"
            placeholder="Quantity"
            value={formData.quantity}
            onChange={handleFormChange}
          />
          {errors.quantity && <div className="error">{errors.quantity}</div>}
        </label>
        <label>
          Item Price:
          <input
            type="number"
            name="price"
            placeholder="Price"
            value={formData.price}
            onChange={handleFormChange}
          />
          {errors.price && <div className="error">{errors.price}</div>}
        </label>
        {editItem !== null ? (
          <button className="btn btn-primary" onClick={handleUpdateItem}>
            Update Item
          </button>
        ) : (
          <button className="btn btn-primary" onClick={handleAddItem}>
            Add Item
          </button>
        )}
      </div>
      <div>
        <h2>Inventory</h2>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Quantity</th>
              <th>Price</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {items.map((item, index) => (
              <tr key={index}>
                <td>{item.name}</td>
                <td>{item.quantity}</td>
                <td>${item.price}</td>
                <td>
                  <button
                    className="btn btn-dark"
                    onClick={() => handleEditItem(index)}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleDeleteItem(index)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};
export default Inventory;
