import React, { useState, useEffect } from "react";
import "./Inventory.css";

const Inventory = () => {
  const [items, setItems] = useState([]);
  const initialValue = {
    name: "",
    quantity: 0,
    price: 0,
  };
  const [formData, setFormData] = useState(initialValue);

  const [editItem, setEditItem] = useState(null);

  const [errors, setErrors] = useState({});
  const [submitting, setSubmitting] = useState(false);

  const validateValues = (formData) => {
    let errors = {};
    if (formData.name === "") {
      errors.name = "Please enter Item Name";
    }
    // if (formData.password.length < 5) {
    //   errors.password = "Password is too short";
    // }
    // if (!formData.age || formData.age < 18) {
    //   errors.age = "Minimum age is 18";
    // }
    return errors;
  };

  
 

  useEffect(() => {
    const finishSubmit = () => {
      console.log(formData);
    };
    if (Object.keys(errors).length === 0 && submitting) {
      finishSubmit();
    }
  }, [errors, submitting, formData]);
  
  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAddItem = () => {
    // if (
    //   formData.name === "" ||
    //   formData.quantity === "" ||
    //   formData.price === ""
    // ) {
    //   alert("Please add empty feilds");
    // } else if (formData.quantity <= 0 || formData.price <= 0) {
    //   alert("Item Quantity or Price Cant be 0 or less than zero");
    // } else {}
      setItems([...items, formData]);
      // console.log(items)
      setFormData(initialValue);
      setErrors(validateValues(formData));
      setSubmitting(true);
    
  };

  const handleUpdateItem = () => {
    if (
      formData.name === "" ||
      formData.quantity === "" ||
      formData.price === ""
    ) {
      alert("Please add empty feilds");
    } else if (formData.quantity <= 0 || formData.price <= 0) {
      alert("Item Quantity or Price Cant be 0 or less than zero");
    } else {
      const updatedItem = [...items];
      // console.log(updatedItem);
      updatedItem[editItem] = formData;
      // console.log(updatedItem);
      setItems(updatedItem);
      setFormData(initialValue);
      setEditItem(null);
    }
  };

  const handleEditItem = (itemKey) => {
    setFormData(items[itemKey]);
    setEditItem(itemKey);
  };

  const handleDeleteItem = (itemKey) => {
    if (window.confirm("Are you sure, you want to delete this item")) {
      setItems(items.filter((_, index) => index !== itemKey));
    }
  };

  return (
    <div  className="container">
      <h1>Inventory Management</h1>
      <div>
        <h2>Add New Item</h2>
        <label>
          Item Name:
          <input
            type="text"
            name="name"
            placeholder="Item Name"
            value={formData.name}
            onChange={handleFormChange}
          />
        </label>
        <label>
          Item Quantity:
          <input
            type="number"
            name="quantity"
            placeholder="Quantity"
            value={formData.quantity}
            onChange={handleFormChange}
          />
        </label>
        <label>
          Item Price:
          <input
            type="number"
            name="price"
            placeholder="Price"
            value={formData.price}
            onChange={handleFormChange}
          />
        </label>
        {editItem !== null ? (
          <button className="btn btn-primary" onClick={handleUpdateItem}>
            Update Item
          </button>
        ) : (
          <button className="btn btn-primary" onClick={handleAddItem}>
            Add Item
          </button>
        )}
      </div>
      <div>
        <h2>Inventory</h2>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Quantity</th>
              <th>Price</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {items.map((item, index) => (
              <tr key={index}>
                <td>{item.name}</td>
                <td>{item.quantity}</td>
                <td>${item.price}</td>
                <td>
                  <button
                    className="btn btn-dark"
                    onClick={() => handleEditItem(index)}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleDeleteItem(index)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};
export default Inventory;
